import{_ as o,c as t,o as e,a5 as n}from"./chunks/framework.DcfbuPOr.js";const a="/assets/image-26.UI63FnGC.png",b=JSON.parse('{"title":"QFontComboBox字体组合框控","description":"","frontmatter":{},"headers":[],"relativePath":"python/PyQt6/232.md","filePath":"python/PyQt6/232.md"}'),c={name:"python/PyQt6/232.md"},r=n('<h1 id="qfontcombobox字体组合框控" tabindex="-1">QFontComboBox字体组合框控 <a class="header-anchor" href="#qfontcombobox字体组合框控" aria-label="Permalink to &quot;QFontComboBox字体组合框控&quot;">​</a></h1><p><code>QFontComboBox</code>控件又称为字体组合框控件，它主要用于在下拉组合框中显示并选择字体。</p><p><code>QFontComboBox</code>继承自<code>QComboBox</code>，自己拥有三个属性，分别是<code>writingSystem</code>，<code>fontFilters</code>，<code>currentFont</code></p><p>Input Widgets -&gt; Font Combo Box</p><h2 id="writingsystem" tabindex="-1">writingSystem <a class="header-anchor" href="#writingsystem" aria-label="Permalink to &quot;writingSystem&quot;">​</a></h2><p><code>writingSystem</code>是书写系统，书写系统包括一个或多个文字集和一系列规则，一个书写系统至少对应一种语言，可以用书写系统的符号集合和规则比如拼写规则、大小写等来表达对应的语言，如汉字、日文、罗马字等。</p><h2 id="fontfilters" tabindex="-1">fontFilters <a class="header-anchor" href="#fontfilters" aria-label="Permalink to &quot;fontFilters&quot;">​</a></h2><p><code>fontFilters</code>是字体过滤器，以<code>QFontComboBox</code>类中提供了一个<code>setFontFilters()</code>方法，用来设置可以选择的字体，该方法的参数值及说明如下：</p><ul><li><p><code>QFontComboBox.AllFonts：</code>所有字体</p></li><li><p><code>QFontComboBox.ScalableFonts：</code>可以自动伸缩的字体</p></li><li><p><code>QFontComboBox.NonScalableFonts：</code>不自动伸缩的字体</p></li><li><p><code>QFontComboBox.MonospacedFonts：</code>等宽字体</p></li><li><p><code>QFontComboBox.ProportionalFonts：</code>比例字体</p></li></ul><h2 id="currentfont" tabindex="-1">currentFont <a class="header-anchor" href="#currentfont" aria-label="Permalink to &quot;currentFont&quot;">​</a></h2><p>currentFont当前字体</p><p><img src="'+a+'" alt="alt text" loading="lazy"></p>',12),i=[r];function d(s,l,p,m,_,h){return e(),t("div",null,i)}const f=o(c,[["render",d]]);export{b as __pageData,f as default};
